package week1.day2;

public class NegToPos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(".....Negative to Positive.....");
		int num=-25;
		int temp=0;
		if (num<0) {
			System.out.println(num + " The given number is Negative");
		}
			for(int i=0;i>num;i++) {
				temp=num*-1;
			System.out.println(temp + " Number converted to Positive");
			break;
			}
			}
		}
	


